import React, { useState, useEffect } from 'react';
import "bootstrap/dist/css/bootstrap.min.css";
import logo from './logo.svg';
import './App.css';
import Button from 'react-bootstrap/Button';
import Table from 'react-bootstrap/Table';
import Form from 'react-bootstrap/Form';
import FloatingLabel from 'react-bootstrap/FloatingLabel';
import Home from './components/home';

const URL = "http://localhost:5000/";
const usersURL = URL + "user/";

const apiKey = "AIzaSyCsYvd4WAyrngOoTdmfIxVgr9IruQ1y2K4";

const App = () => {

  const [next, setNext] = useState(false);

  const [username, setUsername] = useState("");

  const [user, setUser] = useState({
    username: "",
    location: "",
    projects: []
  });

  const [projects, setProjects] = useState([]);

  const [latitude, setLatitude] = useState(0);
  const [longitude, setLongitude] = useState(0);
  const [city, setCity] = useState("");
  const [country, setCountry] = useState("");

  const validate = () => {
    if (username.length > 0) {
      getUsers();
      handleNext();
    } else {
      alert("Please enter a username");
    }
  }

  //make a request to send the username to the backend to create a user and get the user's data back
  const getUsers = async () => {
    try {
      const res = await fetch(usersURL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username: username })
      });

      const data = await res.json();
      user.username = data.username;
      if (data.location === null) {
        if (navigator.geolocation) {
          navigator.geolocation.getCurrentPosition(
            async (position) => {
              setLatitude(position.coords.latitude);
              setLongitude(position.coords.longitude);
              try {
                const response = await fetch(`https://maps.googleapis.com/maps/api/geocode/json?latlng=${latitude},${longitude}&key=${apiKey}`);
                const data = await response.json();
                if (data.results && data.results.length > 0) {
                  const { address_components } = data.results[0];
                  address_components.forEach(component => {
                    if (component.types.includes('locality')) {
                      setCity(component.long_name);
                    }
                    if (component.types.includes('country')) {
                      setCountry(component.long_name);
                    }
                  });
                }
              } catch (error) {
                console.log('Error fetching city and country:', error);
              }
            },
            (error) => {
              console.log('Error getting geolocation:', error);
            }
          );
        } else {
          console.lon('Geolocation is not supported by this browser.');
        }
        user.location = city + ", " + country;
      }
      user.location = data.location;
      user.projects = data.projects;
    } catch (err) {
      console.log(err);
    }
  }

  const handleNext = () => {
    setTimeout(() => {
      setNext(true);
    }, 1000);
  }

  return (
    <>
      <div className='font mt-5'>
        {next ? <Home user={user}></Home> : <>
          <div style={{
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            height: '100vh',
            flexDirection: 'column',
          }} className='font'>
            <h1 className='font'>Welcome to Git-Connect</h1>
            <p className='font'>Please enter your GitHub username to get started</p>
            <FloatingLabel
              controlId="floatingInput"
              label="Username"
              className="mb-3"
            >
              <Form.Control className="font input" type="text" placeholder="Username" onChange={(e) => setUsername(e.target.value)} />
            </FloatingLabel>
            <Button className="nextButton rounded-pill" onClick={validate} size="md">
              Log In
            </Button>
          </div>
        </>}
      </div>
    </>
  );
}

export default App;
