import React, { useState, useEffect } from 'react';
import "bootstrap/dist/css/bootstrap.min.css";
import logo from './logo.svg';
import './App.css';
import Button from 'react-bootstrap/Button';
import Table from 'react-bootstrap/Table';
import Form from 'react-bootstrap/Form';
import FloatingLabel from 'react-bootstrap/FloatingLabel';
import Home from './components/home';
import { get } from 'mongoose';

const URL = "http://localhost:5000/";
const usersURL = URL + "user/";

const App = () => {

  const [next, setNext] = useState(false);

  const [username, setUsername] = useState("");

  const [user, setUser] = useState({
    username: "",
    location: "",
    projects: []
  });

  const validate = () => {
    return username.length > 0;
  }

  //make a request to send the username to the backend to create a user and get the user's data back
  const getUsers = async () => {
    try {
      const res = await fetch(usersURL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username })
      });

      const data = await res.json();
      user.username = data.username;
      user.location = data.location;
      user.projects = data.projects;
    } catch (err) {
      console.log(err);
    }
  }

  const handleNext = () => {
    if (validate()) {
      getUsers();
      setNext(true);
    }
    alert("Please enter a username");
  }



  return (
    <>
      <div style={{
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        height: '100vh',
        flexDirection: 'column',
      }} className='font'>
        {next ? <Home user={user}></Home> : <>
          <FloatingLabel
            controlId="floatingInput"
            label="Username"
            className="mb-3"
          >
            <Form.Control type="text" placeholder="Username" onChange={(e) => setUsername(e.target.value)} />
          </FloatingLabel>
          <Button className="" onClick={handleNext} size="md">
            Button
          </Button>
        </>}
      </div>
    </>
  );
}

export default App;
