import React, { useState } from 'react';
import Card from 'react-bootstrap/Card';
import ListGroup from 'react-bootstrap/ListGroup';
import Button from 'react-bootstrap/Button';

const ProjectCard = ({ project: { id, name, description, owner, contributors } }) => {

    const [project, setProject] = useState({
        id: id,
        name: name,
        description: description,
        owner: owner,
        contributors: contributors
    });

    const [viewProject, setViewProject] = useState(false);

    return (
        <>
            <Card className="mt-4" style={{ width: '18rem' }}>
                <Card.Body>
                    <Button className="contact-btn rounded-pill font mb-2"  size="sm">
                        {project.name}
                    </Button>
                    <Card.Subtitle className="mb-2 text-muted">Description:</Card.Subtitle>
                    <Card.Text>
                        {project.description}
                    </Card.Text>
                    <ListGroup className="list-group-flush">
                        <ListGroup.Item>Owner: {project.owner}</ListGroup.Item>
                    </ListGroup>
                </Card.Body>
            </Card>
            {/* {viewProject ? <Project project={project} /> : <></>} */}
        </>
    );
}

