import { Button } from 'bootstrap';
import React from 'react';
import Card from 'react-bootstrap/Card';
import ListGroup from 'react-bootstrap/ListGroup';

const ProjectCard = ({ project: { id, name, description, owner, contributors } }) => {

    const [project, setProject] = useState({
        id: id,
        name: name,
        description: description,
        owner: owner,
        contributors: contributors
    });

    const [viewProject, setViewProject] = useState(false);

    return (
        <>
            <Card className="mt-4" style={{ width: '18rem' }}>
                <Card.Body>
                    <Card.Title>{project.name}</Card.Title>
                    <Card.Subtitle className="mb-2 text-muted">Description:</Card.Subtitle>
                    <Card.Text>
                        {project.description}
                    </Card.Text>
                    <ListGroup className="list-group-flush">
                        <ListGroup.Item>Owner: {project.owner}</ListGroup.Item>
                    </ListGroup>
                    <Button variant="primary" onClick={setViewProject(true)}>View Project</Button>
                </Card.Body>
            </Card>
            {viewProject ? <Project project={project} /> : <></>}
        </>
    );
}

const Project = (params) => {

    const [project, setProject] = useState({
        id: params.project.id,
        name: params.project.name,
        description: params.project.description,
        owner: params.project.owner,
        contributors: params.project.contributors
    });

    return (
        <>
            <div>
                <h1>{project.name}</h1>
                <h4>Owner: {project.owner}</h4>
                <h4>Description: {project.description}</h4>
                {project.contributors?.length > 0 ? (
                    <div className='container'>
                        {project.contributors.map((project) => (
                            <h4>Contributor: {project}</h4>
                        ))}
                    </div>
                ) : (
                    <div className='empty'>
                        <h2>No projects found</h2>
                    </div>
                )}
            </div>
        </>
    );
}

export default ProjectCard;
