import React, { useEffect, useState } from 'react';
import Card from 'react-bootstrap/Card';
import ListGroup from 'react-bootstrap/ListGroup';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

const URL = "http://localhost:5000/";
const collaborators = URL + "contributors/";

const ProjectCard = ({ project: { id, name, description, owner } }) => {

    const [project, setProject] = useState({
        id: id,
        name: name,
        description: description,
        owner: owner,
        contributors: []
    });

    const [viewProject, setViewProject] = useState(false);

    const getContributors = async () => {
        try {
            const res = await fetch(collaborators, {
                method: "GET",
                headers: { 
                    "Content-Type": "application/json"},
                project_name: name
            });
            const data = await res.json();
            console.log(data);
            project.contributors = data;
        } catch (err) {
            console.log(err);
        }
    }
            

    const handleClose = () => setViewProject(false);
    const handleShow = () => {
        getContributors();    
        setViewProject(true);
    }

    return (
        <>
            <Card className="mt-4" style={{ width: '18rem' }}>
                <Card.Body>
                    <Button className="contact-btn rounded-pill font mb-2" onClick={handleShow} size="sm">
                        {project.name}
                    </Button>
                    <Card.Subtitle className="mb-2 text-muted">Description:</Card.Subtitle>
                    <Card.Text>
                        {project.description}
                    </Card.Text>
                    <ListGroup className="list-group-flush">
                        <ListGroup.Item>Owner: {project.owner}</ListGroup.Item>
                    </ListGroup>
                </Card.Body>
            </Card>

            <Modal  show={viewProject} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>{name}</Modal.Title>
                </Modal.Header>

                <Modal.Body>
                    <h4>Contributors:</h4>
                    {project.contributors?.length > 0 ? (
                        <div className='container'>
                            {project.contributors.map((contributor) => (
                                <h4 key={contributor.id}>{contributor}</h4>
                            ))}
                        </div>
                    ) : (
                        <div className='empty'>
                            <h2>No contributors found</h2>
                        </div>
                    )}
                </Modal.Body>

                <Modal.Footer>
                    <Button variant="secondary">Close</Button>
                    <Button variant="primary">Save changes</Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}

export default ProjectCard;
